import 'package:flutter/material.dart'; import '../tokens/colors.dart';
class PulseButton extends StatefulWidget{ final String label; final IconData icon; final VoidCallback onTap; const PulseButton({super.key, required this.label, required this.icon, required this.onTap}); @override State<PulseButton> createState()=>_PulseButtonState(); }
class _PulseButtonState extends State<PulseButton> with SingleTickerProviderStateMixin{ late AnimationController _c; @override void initState(){ super.initState(); _c=AnimationController(vsync:this,duration:const Duration(seconds:2))..repeat(reverse:true);} @override void dispose(){ _c.dispose(); super.dispose(); }
  @override Widget build(BuildContext context){ return AnimatedBuilder(animation:_c,builder:(_,__){ final scale=1.0+(_c.value*0.02); return Transform.scale(scale:scale, child: InkWell(onTap:widget.onTap,borderRadius:BorderRadius.circular(16), child: Container(height:56, decoration: BoxDecoration(borderRadius: BorderRadius.circular(16), gradient: const LinearGradient(colors:[TalaaColors.royalBlue, TalaaColors.indigo])), child: Center(child: Row(mainAxisAlignment: MainAxisAlignment.center, children:[ Icon(widget.icon, color: Colors.white), const SizedBox(width:8), Text(widget.label, style: const TextStyle(color: Colors.white, fontWeight: FontWeight.w700, fontSize: 18)), ]))),); }); } }
